mapper.select:
select *
from EVENT_TB
where user_id = #{user_id}
  and review_id = #{review_id}

select *
from EVENT_TB e1
         left join (
    select max(createdAt) max
    from EVENT_TB
    where type = #{type}
      and user_id = #{user_id}
      and place_id = #{place_id}
) e2 on max = e1.createdAt
where max is not null

mapper.delete:
delete
from mileage.EVENT_TB
where user_id = #{user_id}
  and review_id = #{review_id}
