mapper.select:
select *
from mileage.REVIEW_TB
where place_id = #{place_id}
  and status_code = 1
  and user_id != #{user_id}
limit 1

select *
from mileage.REVIEW_TB
where review_id = #{review_id}
  and status_code = 1;

mapper.update:
update mileage.REVIEW_TB
set status_code = 0
where review_id = #{review_id}
