mapper.select:
select *
from EVENT_TB
where review_id = #{review_id}
  and user_id = #{user_id}
  and place_id = #{place_id}

mapper.update:
update EVENT_TB
set content=#{content},
    photo_url=#{photo_url}
where review_id = #{review_id}
  and user_id = #{user_id}
  and place_id = #{place_id}

mapper.delete:
delete
from EVENT_TB
where review_id = #{review_id}
  and user_id = #{user_id}
  and place_id = #{place_id}
